template.declaration:
// Place local declarations here.

//stack grootte
const int size = 2;

//stack pointer to current element
int[0,size] head=0;

//------------------------------
// The stack
//------------------------------

//id_e boatsInQueue[size];//boatsInQueue[0] is left side, boatsInQueue[1] is right side

id_e boatsInQueueLeft = 0;
id_e boatsInQueueRight = 0;


//stack push
void push(id_e e)
{
    if(head==size){
        head=0;
    }

    if(head==0){
        boatsInQueueLeft = e;
    } else if(head==1){
        boatsInQueueRight = e;
    }

    head++;

    /*if(boatsInQueue[head]==0){
        boatsInQueue[head++]=e;
    }*/
}
//empty stack completely
void emptyStack()
{
    /*id_e temp[size];
    boatsInQueue = temp;*/
    head = 0;
    //remember 

    boatsInQueueLeft = 0;
    boatsInQueueRight = 0;
}
//stack pop
/*id_e pop()
{
    id_e temp=boatsInQueue[head-1];
    boatsInQueue[--head]=0;
    return (boatsInQueue[--head]);
}
//stack push
bool isFull()
{
    return(head==size);

}*/
//any boats at the sluice?
bool isEmpty()
{
    if (boatsInQueueLeft==0&&boatsInQueueRight==0){
        return true;
    }
    return false;

    /*if (boatsInQueue[0]==0&&boatsInQueue[1]==0){
        return true;
    }
    return false;*/
    //return (head==0);
}

/*end stack*/

void initialize(){
    waterLevelLeft = 3;
    waterLevelLockChamber = 2;
    waterLevelRight = 1;
}
void fillLockChamber(){   
    if(waterLevelLeft > waterLevelLockChamber){
        waterLevelLockChamber = waterLevelLockChamber+1;
    }
}

void emptyLockChamber(){
    if(waterLevelRight < waterLevelLockChamber){
        waterLevelLockChamber = waterLevelLockChamber-1;
    }

bool waterLevelCheckLeftSame(){
    if(waterLevelLeft == waterLevelLockChamber){
        return true;//keep level
    } else if(waterLevelLeft < waterLevelLockChamber){
        return emptyLockChamber;
    } else if(waterLevelLeft > waterLevelLockChamber){
        return fillLockChamber;
    }
    return false;
}
bool waterLevelCheckRightSame(){
    if(waterLevelRight == waterLevelLockChamber){
        return true;//keep level
    } else if(waterLevelRight < waterLevelLockChamber){
        return emptyLockChamber;
    } else if(waterLevelRight > waterLevelLockChamber){
        return fillLockChamber;
    }
    return false;
}



bool passRequestPriority(){ //decides which que is going trough the sluice
    if(boatsInQueueLeft==1&&boatsInQueueRight==1){//Give priority to left
        return waterLevelCheckLeftSame();
    } else if(boatsInQueueLeft==1&&boatsInQueueRight==0){//Priority to left
        return waterLevelCheckLeftSame();
    } else if(boatsInQueueLeft==0&&boatsInQueueRight==1){//Priority to Right
        return waterLevelCheckRightSame();
    }
    return true;//shouldn't be able to get here, make emergency state
}

